import React, { useEffect, useRef } from "react";

const UseRef1 = () => {
  const inputRef = useRef();

  useEffect(() => {
    // Focus vào input ngay khi được mount
    inputRef.current.focus();
  }, []);

  return <input ref={inputRef} />;
};

export default UseRef1;
