import React, { useEffect, useMemo, useState } from "react";

const UseMemo = () => {
  const [count, setCount] = useState(0);

  // Sử dụng useMemo để memoize giá trị của giai thừa
  //   const factorial = useMemo(() => {
  //     console.log("Calculating factorial...");
