  //       console.log("i", i);
  //       result *= i;
  //     }
  //     console.log("result", result);
  //     return result;
  //   }, [count]);
  const factorial = () => {
    let result = 1;
    for (let i = 1; i <= count; i++) {
      console.log("i", i);
      result *= i;
    }
    console.log("result", result);
    return result;
  };
  useEffect(() => {
    // const factorial = () =>{
    //     let result = 1;
    //     for (let i = 1; i <= count; i++) {
    //       console.log("i", i);
    //       result *= i;
    //     }
    //     console.log("result", result);
    //     return result;
    // }
    factorial();
  }, [factorial]);

  return (
    <div>
      <p>Count: {count}</p>
      <p>Factorial: {factorial}</p>
      {/* Truyền hàm increment vào component con */}
      <button onClick={() => setCount(count + 1)}>Increment</button>
    </div>
  );
};

export default UseMemo;
