import React, { useRef } from "react";

const UseRef1 = () => {
  const inputRef = useRef(null);

  const focusInput = () => {
    // Gọi focus() trên tham chiếu của input khi nút được nhấn
    inputRef.current.focus();
  };

  return (
    <div>
      {/* Tham chiếu đến input bằng useRef */}
      <input ref={inputRef} type="text" />
      {/* Khi nút được nhấn, focus sẽ được áp dụng vào input */}
      <button onClick={focusInput}>Focus Input</button>
    </div>
  );
};

export default UseRef1;
