import React, { useEffect, useRef } from "react";

/** Truy cập và thao tác thực hiện với DOM element*/
const UseRef1 = () => {
  const inputRef = useRef();

  useEffect(() => {
    // Focus vào input ngay khi được mount
    inputRef.current.focus();
  }, []);

  return <input ref={inputRef} />;
};

/** Lưu trữ giá trị không gây ra re-render*/
const UseRef2 = () => {
  const countRef = useRef(0);

  useEffect(() => {
    // This effect runs after every render
    console.log("Render count:", countRef.current);
    // Update the count without triggering re-render
    countRef.current += 1;
  });

  return <div>Check the console for render count</div>;
};
export { UseRef1, UseRef2 };
