import "./App.css";
import { UseEffect1, UseEffect2, UseEffect3 } from "./use-effect/useEffect";
import { UseRef1, UseRef2, UseRef3 } from "./use-ref/UseRef";
import Couter from "./use-state/Couter";
import FormHandling from "./use-state/FormHandling";
import Modal from "./use-state/Modal";

function App() {
  return (
    <div className="App">
      <div>
        <h2>Couter</h2>
        <Couter />
        <h2>Modal</h2>
        <Modal />
        <h2>Form Handling</h2>
        <FormHandling />
      </div>
      <hr />
      <div>
        <h2>UseEffect 1</h2>
        <UseEffect1 />
        <h2>UseEffect 2</h2>
        <UseEffect2 />
        <h2>UseEffect 3 (scroll)</h2>
        <UseEffect3 />
      </div>
      <hr />
      <div>
        <h2>UseRef 1</h2>
        <UseRef1 />
        <h2>UseRef 2</h2>
        <UseRef2 />
        <h2>UseRef 3</h2>
        <UseRef3 />
      </div>
    </div>
  );
}

export default App;
